import React from 'react';
import { useNavigate } from 'react-router-dom';
import { adminNavItems } from './AdminSidebar';

const AdminBottomNav = ({ activeTab, setActiveTab }) => {
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };
  return (
    <nav className="admin-bottom-nav">
      {adminNavItems.slice(0, 4).map(item => (
        <button
          key={item.id}
          onClick={() => setActiveTab(item.id)}
          className={`admin-bottom-nav-link ${activeTab === item.id ? 'active' : ''}`}
        >
          <span className="icon">{item.icon}</span>
          <span className="label">{item.label}</span>
        </button>
      ))}
      <button className="admin-bottom-nav-link logout" onClick={handleLogout}>
        <span className="icon">ðŸšª</span>
        <span className="label">Logout</span>
      </button>
      <style>{`
        .admin-bottom-nav {
          display: none;
        }
        @media (max-width: 1024px) {
          .admin-bottom-nav {
            display: flex;
            position: fixed;
            bottom: 0;
            left: 0;
            right: 0;
            height: 60px;
            background: #23272F;
            border-top: 1px solid rgba(46, 204, 113, 0.3);
            z-index: 1200;
            justify-content: space-around;
            align-items: center;
            box-shadow: 0 -2px 12px rgba(0,0,0,0.13);
          }
          .admin-bottom-nav-link {
            background: none;
            border: none;
            color: #E5E7EB;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            font-size: 1rem;
            font-weight: 600;
            padding: 0.2rem 0.5rem;
            border-radius: 0.7rem;
            transition: background 0.18s, color 0.18s;
            text-decoration: none;
            min-width: 48px;
          }
          .admin-bottom-nav-link .icon {
            font-size: 1.2rem;
            margin-bottom: 2px;
          }
          .admin-bottom-nav-link .label {
            font-size: 0.7rem;
          }
          .admin-bottom-nav-link.active {
            color: #2ECC71;
            background: rgba(46,204,113,0.10);
          }
          .admin-bottom-nav-link.logout {
            color: #FF6B35;
          }
        }
      `}</style>
    </nav>
  );
};

export default AdminBottomNav;
